<script>
import emailjs from '@emailjs/browser';

export default {
    name: "Contact",
    data() {
        return {
            form: {
                recipient: "test@test.com",
                message: "test message test",
            },
            loading: false,
            successMsg: '',
            errorMsg: '',
        };
    },
    methods: {
        sendEmail() {
            this.loading = true;
            emailjs
                .sendForm(
                    'service_ckzybfc', // service id
                    'template_gist8xc', // template id
                    this.$refs.form,
                    'MlznwHCInh_8-Q20t' //public key
                )
                .then((response) => {
                    this.form = {};
                    this.loading = false;
                    this.successMsg = 'Žinutė išsiųsta.'
                    setTimeout(() => {
                        this.successMsg = '';
                    }, 5000);
                })
                .catch((error) => {
                    this.loading = false;
                    this.errorMsg = 'Įvyko klaida. Bandykite dar kartą.'
                    setTimeout(() => {
                        this.errorMsg = '';
                    }, 5000);
                });
        },
    },
};
